AFRAME.registerComponent("seek-position",{schema:{type:"vec3"},init:function(){var self=this;self.targetPos=this.data;self.isSeeking=true},update:function(oldData){this.targetPos=this.data;this.isSeeking=true},tick:function(){if(this.isSeeking)this.move()},move:function(){var positionAttribute=this.el.getAttribute("position");var position=new THREE.Vector3(positionAttribute.x,positionAttribute.y,positionAttribute.z);position.lerp(this.targetPos,.1);this.el.setAttribute("position",position);var delta=this.el.object3D.position.distanceToSquared(this.targetPos);if(delta<1e-4){this.isSeeking=false}}});